package Loopdemo;

public class Whiledemo 
{
	int i=10;
	public void acc()
	{
		while(i<=100);
		{
		System.out.println("Hello"+i);
		i=i+10;
	}
}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   Whiledemo oo=new Whiledemo();
   oo.acc();
	}
}



